// Get data from previous nodes
// Code node'undan gelen flattened config
const flatConfig = $('Code').first().json;

// SQL query sonucunu al
const brandDataResult = $input.all();

// Brand validation - SQL'den gelen veriyi kontrol et
if (!brandDataResult || brandDataResult.length === 0) {
  throw new Error('Brand not found in database');
}

const brandData = brandDataResult[0].json;

if (!brandData || !brandData.name) {
  throw new Error('Brand data is invalid');
}

const brand = brandData.name;
const brandDisplayName = brandData.display_name;
const subBrands = brandData.sub_brands || {};

// Config'den subbrand ve diÄŸer ayarlarÄ± al
const subbrand = flatConfig.subbrand || 'all';
const dateRange = flatConfig.dateRange || {
  start: new Date(Date.now() - 30*24*60*60*1000).toISOString().split('T')[0],
  end: new Date().toISOString().split('T')[0]
};

// Sub-brand keywords ve hashtags'leri otomatik Ã§ek
let brandKeywords = [brand, `#${brand}`, `@${brand}`];
let campaignHashtags = [];
let instagramAccounts = [];
let tiktokAccounts = [];

// EÄŸer specific bir subbrand seÃ§ildiyse
if (subbrand !== 'all' && subBrands && subBrands[subbrand]) {
  const subbrandData = subBrands[subbrand];

  // Hashtags ekle
  if (subbrandData.hashtags && Array.isArray(subbrandData.hashtags)) {
    campaignHashtags = [...campaignHashtags, ...subbrandData.hashtags];
    brandKeywords = [...brandKeywords, ...subbrandData.hashtags];
  }

  // Instagram account ekle
  if (subbrandData.instagram && subbrandData.instagram.username) {
    instagramAccounts.push(subbrandData.instagram.username);
    brandKeywords.push(`@${subbrandData.instagram.username}`);
  }

  // TikTok account ekle  
  if (subbrandData.tiktok && subbrandData.tiktok.username) {
    tiktokAccounts.push(subbrandData.tiktok.username);
    brandKeywords.push(`@${subbrandData.tiktok.username}`);
  }
} else if (subbrand === 'all' && subBrands) {
  // TÃ¼m subbrand'lerin keyword'lerini topla
  Object.values(subBrands).forEach(subbrandData => {
    if (subbrandData && typeof subbrandData === 'object') {
      if (subbrandData.hashtags && Array.isArray(subbrandData.hashtags)) {
        campaignHashtags = [...campaignHashtags, ...subbrandData.hashtags];
        brandKeywords = [...brandKeywords, ...subbrandData.hashtags];
      }
      if (subbrandData.instagram && subbrandData.instagram.username) {
        instagramAccounts.push(subbrandData.instagram.username);
        brandKeywords.push(`@${subbrandData.instagram.username}`);
      }
      if (subbrandData.tiktok && subbrandData.tiktok.username) {
        tiktokAccounts.push(subbrandData.tiktok.username);
        brandKeywords.push(`@${subbrandData.tiktok.username}`);
      }
    }
  });
}

// Unique keywords only
brandKeywords = [...new Set(brandKeywords)];
campaignHashtags = [...new Set(campaignHashtags)];
instagramAccounts = [...new Set(instagramAccounts)];
tiktokAccounts = [...new Set(tiktokAccounts)];

return [{
  json: {
    brand: brand,
    brandDisplayName: brandDisplayName,
    subbrand: subbrand,
    subBrandDetails: subbrand !== 'all' ? subBrands[subbrand] : null,
    dateRange: dateRange,
    brand_keywords: brandKeywords,
    campaign_hashtags: campaignHashtags,
    instagram_accounts: instagramAccounts,
    tiktok_accounts: tiktokAccounts,
    competitor_keywords: flatConfig.competitor_keywords || [],
    filter_by_keywords: flatConfig.filter_by_keywords || (subbrand !== 'all'),
    analysis_window_days: flatConfig.analysis_window_days || 30,
    platforms: flatConfig.platforms || ['instagram', 'tiktok'],
    limit: flatConfig.limit || 100,
    requestId: flatConfig.requestId || `sentiment_${Date.now()}`,
    metadata: {
      brandInfo: brandData,
      totalSubBrands: Object.keys(subBrands).length,
      originalConfig: flatConfig
    }
  }
}];

// Prepare Comments for Analysis
const comments = $input.all();
const config = $('Process Config').first().json;

const prepared = comments.map(item => {
  const data = item.json || {};
  const platform = (data.platform || '').toLowerCase() || 'instagram';
  const brand = data.brand || config.brand || 'unknown';

  let cleanText = (data.comment_text || '')
    .replace(/&quot;/g, '"')
    .replace(/&apos;/g, "'")
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&amp;/g, '&');

  const mentions = cleanText.match(/@[\w.]+/g) || [];
  const hashtags = cleanText.match(/#[\w\u0080-\uFFFF]+/g) || [];

  const spamIndicators = [];
  if (/(.)\1{4,}/.test(cleanText)) spamIndicators.push('repeated_chars');
  const upperRatio = (cleanText.match(/[A-Z]/g) || []).length / Math.max(cleanText.length, 1);
  if (upperRatio > 0.6 && cleanText.length > 10) spamIndicators.push('excessive_caps');
  if (/https?:\/\/|www\./i.test(cleanText)) spamIndicators.push('contains_link');

  const uniqueTimestamp = Date.now();
  const randomString = Math.random().toString(36).substring(7);
  const finalCommentId = `${data.comment_id || 'no_id'}_${uniqueTimestamp}_${randomString}`;

  return {
    json: {
      platform,
      brand,
      post_id: data.post_id,
      comment_id: finalCommentId,
      brand_keywords: config.brand_keywords,
      competitor_keywords: config.competitor_keywords,
      campaign_hashtags: config.campaign_hashtags,
      commenter_username: data.commenter_username || 'unknown_user',
      comment_text: cleanText,
      comment_date: data.comment_date,
      comment_likes: parseInt(data.comment_likes) || 0,
      post_caption: data.post_caption || '',
      post_owner: data.post_owner || '',
      post_likes: Number(data.likes_count) || 0,
      post_comments: Number(data.comments_count) || 0,
      mentions,
      hashtags,
      text_length: cleanText.length,
      word_count: cleanText.split(/\s+/).filter(w => w.length > 0).length,
      spam_indicators: spamIndicators,
      potential_spam: spamIndicators.length > 1,
    }
  };
}).filter(item => item.json.post_id);

return prepared;

// Parse AI responses and enrich
const allResponses = $input.all();
const config = $('Process Config').first().json;
const results = [];

allResponses.forEach((item, index) => {
  const aiResponse = item.json;
  let aiAnalysis = {};

  try {
    // AI response'u parse et
    if (aiResponse.choices && aiResponse.choices[0]) {
      const content = aiResponse.choices[0].message.content;
      aiAnalysis = typeof content === 'string' ? JSON.parse(content) : content;
    } else if (aiResponse.message && aiResponse.message.content) {
      const content = aiResponse.message.content;
      aiAnalysis = typeof content === 'string' ? JSON.parse(content) : content;
    } else if (typeof aiResponse === 'string') {
      aiAnalysis = JSON.parse(aiResponse);
    } else {
      aiAnalysis = aiResponse;
    }
  } catch (error) {
    console.error('Parse error for item', index, ':', error);

    // Default deÄŸerler
    aiAnalysis = {
      sentiment_score: 0,
      sentiment_label: 'neutral',
      confidence_score: 50,
      primary_emotion: 'neutral',
      mentions_brand: false,
      brand_sentiment: 0,
      is_question: false,
      is_complaint: false,
      requires_response: false,
      response_priority: 'low',
      purchase_intent: 'none',
      is_spam: false,
      content_relevance: 'medium',
      content_relevance_score: 50,
      is_advertisement: false,
      spam_type: null
    };
  }

  // Original comment verisini al
  const originalComments = $items('Prepare Comments');
  const original = originalComments[index]?.json || {};

  // Eksik alanlarÄ± doldur - YENÄ° ALANLAR EKLENDÄ°
  const enrichedAnalysis = {
    ...original,
    ...aiAnalysis,

    // Ensure all required fields exist
    sentiment_score: aiAnalysis.sentiment_score || 0,
    sentiment_label: aiAnalysis.sentiment_label || 'neutral',
    confidence_score: aiAnalysis.confidence_score || 50,
    primary_emotion: aiAnalysis.primary_emotion || 'neutral',

    mentions_brand: aiAnalysis.mentions_brand || false,
    brand_sentiment: aiAnalysis.brand_sentiment || 0,

    is_question: aiAnalysis.is_question || false,
    is_complaint: aiAnalysis.is_complaint || false,
    requires_response: aiAnalysis.requires_response || false,
    response_priority: aiAnalysis.response_priority || 'low',

    purchase_intent: aiAnalysis.purchase_intent || 'none',

    is_spam: aiAnalysis.is_spam || false,

    // YENÄ° ALANLAR
    content_relevance: aiAnalysis.content_relevance || 'medium',
    content_relevance_score: aiAnalysis.content_relevance_score || 50,
    is_advertisement: aiAnalysis.is_advertisement || false,
    spam_type: aiAnalysis.spam_type || null,

    analyzed_at: new Date().toISOString()
  };

  results.push({
    json: enrichedAnalysis
  });
});

console.log(`Parsed ${results.length} AI responses with relevance data`);
return results;

// Generate sentiment analysis summary
const allComments = $input.all().map(item => item.json);
const config = $('Process Config').first().json;

// Debug log
console.log('Total comments received:', allComments.length);
console.log('Sample comment:', allComments[0]);

// Calculate metrics
const totalComments = allComments.length;

// EÄŸer hiÃ§ yorum yoksa
if (totalComments === 0) {
  return [{
    json: {
      analysisType: 'sentiment',
      requestId: config.requestId || `sentiment_${Date.now()}`,
      brand: config.brand,
      subbrand: config.subbrand || 'all',
      dateRange: config.dateRange,
      data: {
        error: 'No comments found for analysis',
        sentimentMetrics: {
          totalAnalyzed: 0,
          validComments: 0,
          spamFiltered: 0,
          avgSentiment: 0,
          sentimentDistribution: { positive: 0, negative: 0, neutral: 0 }
        }
      }
    }
  }];
}

// Spam filtreleme
const spamComments = allComments.filter(c => c.is_spam === true).length;
const validComments = totalComments - spamComments;

// Sentiment daÄŸÄ±lÄ±mÄ±
const sentimentCounts = {
  positive: 0,
  negative: 0,
  neutral: 0
};

// Sentiment score toplamÄ± iÃ§in
let totalSentimentScore = 0;
let scoredComments = 0;

// Brand mention metrikleri
let brandMentions = 0;
let totalBrandSentiment = 0;
let brandSentimentCount = 0;

// Engagement metrikleri
let questionsCount = 0;
let complaintsCount = 0;
let requiresResponseCount = 0;
let highPriorityCount = 0;

// Purchase intent
const purchaseIntentDist = {
  high: 0,
  medium: 0,
  low: 0,
  none: 0
};

// Emotion counts
const emotionCounts = {};

// Platform breakdown
const platformBreakdown = {
  instagram_comment_count: 0,
  tiktok_comment_count: 0
};

// Relevance metrics - YENÄ°
const relevanceMetrics = {
  high_relevance: 0,
  medium_relevance: 0,
  low_relevance: 0,
  irrelevant: 0,
  advertisements: 0
};

// Priority comments array
const priorityComments = [];
const allHighPriorityComments = []; // TÃ¼m high priority yorumlarÄ± topla

// Her yorumu iÅŸle
allComments.forEach(comment => {
  // Platform
  const platform = (comment.platform || '').toLowerCase();
  if (platform === 'instagram') {
    platformBreakdown.instagram_comment_count++;
  } else if (platform === 'tiktok') {
    platformBreakdown.tiktok_comment_count++;
  }

  // Relevance metrics - YENÄ°
  const relevance = comment.content_relevance || 'medium';
  if (relevance === 'high') relevanceMetrics.high_relevance++;
  else if (relevance === 'medium') relevanceMetrics.medium_relevance++;
  else if (relevance === 'low') relevanceMetrics.low_relevance++;
  else if (relevance === 'irrelevant') relevanceMetrics.irrelevant++;

  if (comment.is_advertisement === true) {
    relevanceMetrics.advertisements++;
  }

  // Skip spam for metrics
  if (comment.is_spam === true) {
    return;
  }

  // Sentiment distribution
  const sentimentLabel = comment.sentiment_label || 'neutral';
  sentimentCounts[sentimentLabel]++;

  // Sentiment score
  if (typeof comment.sentiment_score === 'number') {
    totalSentimentScore += comment.sentiment_score;
    scoredComments++;
  }

  // Brand mentions
  if (comment.mentions_brand === true) {
    brandMentions++;
    if (typeof comment.brand_sentiment === 'number') {
      totalBrandSentiment += comment.brand_sentiment;
      brandSentimentCount++;
    }
  }

  // Engagement metrics
  if (comment.is_question === true) questionsCount++;
  if (comment.is_complaint === true) complaintsCount++;
  if (comment.requires_response === true) requiresResponseCount++;

  // Priority - GÃœNCELLENDÄ°
  if (comment.response_priority === 'high') {
    highPriorityCount++;

    // TÃ¼m high priority yorumlarÄ± topla
    allHighPriorityComments.push({
      username: comment.commenter_username || 'unknown',
      text: (comment.comment_text || '').substring(0, 100),
      reason: comment.is_complaint ? 'complaint' : 
              comment.is_question ? 'question' : 
              'negative sentiment',
      platform: comment.platform || 'unknown',
      relevance: comment.content_relevance || 'unknown',
      relevance_score: comment.content_relevance_score || 0,
      is_advertisement: comment.is_advertisement || false
    });
  }

  // Purchase intent
  const intent = comment.purchase_intent || 'none';
  if (purchaseIntentDist.hasOwnProperty(intent)) {
    purchaseIntentDist[intent]++;
  }

  // Emotions
  const emotion = comment.primary_emotion || 'neutral';
  emotionCounts[emotion] = (emotionCounts[emotion] || 0) + 1;
});

// Priority comments filtreleme - YENÄ°
// AlakalÄ± ve reklam olmayan high priority yorumlarÄ± seÃ§
const filteredPriorityComments = allHighPriorityComments
  .filter(c => 
    c.relevance !== 'irrelevant' && 
    !c.is_advertisement
  )
  .sort((a, b) => b.relevance_score - a.relevance_score) // En alakalÄ±larÄ± Ã¼ste
  .slice(0, 5);

// Calculate averages
const avgSentiment = scoredComments > 0 ? totalSentimentScore / scoredComments : 0;
const avgBrandSentiment = brandSentimentCount > 0 ? totalBrandSentiment / brandSentimentCount : 0;

// Top emotions
const topEmotions = Object.entries(emotionCounts)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([emotion, count]) => ({
    emotion,
    count,
    percentage: validComments > 0 ? ((count / validComments) * 100).toFixed(1) : '0'
  }));

// Platform percentages - YENÄ°
const platformPercentages = {
  instagram_percentage: totalComments > 0 ? 
    ((platformBreakdown.instagram_comment_count / totalComments) * 100).toFixed(1) : '0',
  tiktok_percentage: totalComments > 0 ? 
    ((platformBreakdown.tiktok_comment_count / totalComments) * 100).toFixed(1) : '0'
};

// Relevance percentages - YENÄ°
const relevancePercentages = {
  high_relevance_percentage: totalComments > 0 ? 
    ((relevanceMetrics.high_relevance / totalComments) * 100).toFixed(1) : '0',
  medium_relevance_percentage: totalComments > 0 ? 
    ((relevanceMetrics.medium_relevance / totalComments) * 100).toFixed(1) : '0',
  low_relevance_percentage: totalComments > 0 ? 
    ((relevanceMetrics.low_relevance / totalComments) * 100).toFixed(1) : '0',
  irrelevant_percentage: totalComments > 0 ? 
    ((relevanceMetrics.irrelevant / totalComments) * 100).toFixed(1) : '0',
  advertisement_percentage: totalComments > 0 ? 
    ((relevanceMetrics.advertisements / totalComments) * 100).toFixed(1) : '0'
};

// Final output
return [{
  json: {
    analysisType: 'sentiment',
    requestId: config.requestId || `sentiment_${Date.now()}`,
    brand: config.brand,
    subbrand: config.subbrand || 'all',
    dateRange: config.dateRange,

    data: {
      sentimentMetrics: {
        totalAnalyzed: totalComments,
        validComments: validComments,
        spamFiltered: spamComments,
        avgSentiment: parseFloat(avgSentiment.toFixed(3)),
        sentimentDistribution: sentimentCounts,
        positiveRate: validComments > 0 ? 
          ((sentimentCounts.positive / validComments) * 100).toFixed(1) : '0',
        negativeRate: validComments > 0 ? 
          ((sentimentCounts.negative / validComments) * 100).toFixed(1) : '0',
        neutralRate: validComments > 0 ? 
          ((sentimentCounts.neutral / validComments) * 100).toFixed(1) : '0'
      },

      brandMetrics: {
        brandMentions: brandMentions,
        mentionRate: totalComments > 0 ? 
          ((brandMentions / totalComments) * 100).toFixed(1) : '0',
        avgBrandSentiment: parseFloat(avgBrandSentiment.toFixed(3)),
        brandPerception: avgBrandSentiment > 0.5 ? 'positive' : 
                        avgBrandSentiment < -0.5 ? 'negative' : 'neutral'
      },

      engagementMetrics: {
        questionsCount: questionsCount,
        complaintsCount: complaintsCount,
        requiresResponse: requiresResponseCount,
        highPriorityCount: highPriorityCount,
        priorityComments: filteredPriorityComments, // FiltrelenmiÅŸ yorumlar
        relevanceMetrics: relevanceMetrics, // YENÄ°
        relevancePercentages: relevancePercentages // YENÄ°
      },

      purchaseIntent: purchaseIntentDist,

      emotionalAnalysis: {
        topEmotions: topEmotions,
        dominantEmotion: topEmotions[0]?.emotion || 'neutral',
        emotionDistribution: emotionCounts
      },

      platformBreakdown: {
        ...platformBreakdown,
        ...platformPercentages // YÃ¼zdeler eklendi
      },

      summary: {
        message: `Analyzed ${totalComments} comments with ${validComments > 0 ? ((sentimentCounts.positive / validComments) * 100).toFixed(0) : 0}% positive sentiment`,
        overallSentiment: avgSentiment > 0.3 ? 'Positive' : 
                         avgSentiment < -0.3 ? 'Negative' : 'Neutral',
        actionRequired: highPriorityCount > 0,
        relevantContentRate: totalComments > 0 ? 
          (((relevanceMetrics.high_relevance + relevanceMetrics.medium_relevance) / totalComments) * 100).toFixed(1) : '0'
      }
    },

    metadata: {
      processedAt: new Date().toISOString(),
      commentsAnalyzed: totalComments,
      executionTime: 0
    }
  }
}];

// Flatten Config node
const input = $input.first().json;

// Config'i dÃ¼zleÅŸtir
let flatConfig = {};

if (input.config) {
  // Orkestrasyon'dan gelen format
  flatConfig = {
    ...input.config,
    originalInput: input
  };
} else if (input.brand) {
  // Zaten dÃ¼z format
  flatConfig = input;
} else {
  throw new Error('No valid configuration found');
}

return [{
  json: flatConfig
}];
