// Get the transcript array from the input
// Add error handling for missing or invalid transcripts

try {
  const inputData = items[0].json;

  // Check if there was an error in the API response
  if (inputData.success === false || inputData.error) {
    return [{
      json: {
        error: true,
        message: inputData.error || 'Transcript not available',
        transcript: [],
        hasValidTranscript: false
      }
    }];
  }

  const transcript = inputData.transcript;

  // Check if transcript exists and is an array
  if (!transcript || !Array.isArray(transcript) || transcript.length === 0) {
    return [{
      json: {
        error: true,
        message: 'No valid transcript found',
        transcript: [],
        hasValidTranscript: false
      }
    }];
  }

  // Map through transcript items and add timestamp
  const updatedTranscript = transcript.map(entry => {
    const offsetInSeconds = entry.offset;

    // Calculate hours, minutes and seconds
    const hours = Math.floor(offsetInSeconds / 3600);
    const minutes = Math.floor((offsetInSeconds % 3600) / 60);
    const seconds = Math.floor(offsetInSeconds % 60);

    // Pad minutes and seconds with leading zero if < 10
    const formattedMinutes = minutes < 10 ? `0${minutes}` : `${minutes}`;
    const formattedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;

    // Build final timestamp string
    const timestamp =
      hours > 0
        ? `${hours}:${formattedMinutes}:${formattedSeconds}` // HH:MM:SS
        : `${minutes}:${formattedSeconds}`;                  // MM:SS (if under 1h)

    // Return updated entry
    return {
      ...entry,
      timestamp
    };
  });

  // Return as n8n output
  return [{
    json: {
      transcript: updatedTranscript,
      error: false,
      hasValidTranscript: true
    }
  }];

} catch (error) {
  // Return error but don't fail the workflow
  return [{
    json: {
      error: true,
      message: error.message || 'Failed to process transcript',
      transcript: [],
      hasValidTranscript: false
    }
  }];
}

